package projectDB;import java.awt.BorderLayout;import java.awt.GridLayout;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;import javax.swing.JButton;import javax.swing.JFrame;import javax.swing.JLabel;import javax.swing.JOptionPane;import javax.swing.JPanel;import javax.swing.JScrollPane;import javax.swing.JTable;import javax.swing.JTextField;import javax.swing.SwingUtilities;import javax.swing.table.DefaultTableModel;// 학생 성적 관리 시스템 개발// 1) GUI -> Windows -> 학생이름, 중간시험성적, 기말시험성적, 평균 (name, midtermField, finaltermField(전역변수 -> 필드 선언))// 2) 버튼 이벤트 - 추가(학생정보), 삭제(학생정보)// 3) 계산 : 평균(중간시험성적 + 기말시험성적)/2// 4) 입력화면 Layout, 출력화면 Layout// 5) 수 데이터 -> Chart Graph 출력(시각적 출력)// 6) GUI DashBoardpublic class StGUI01 extends JFrame {	// 필드 선언	private JTable table;	private DefaultTableModel tableModel;	private JTextField nameField, midtermField, finaltermField;		// 생성자 선언	public StGUI01() {		setTitle("학생 성적 관리 GUI");		setSize(800, 400);		setDefaultCloseOperation(EXIT_ON_CLOSE);		setLocationRelativeTo(null);				// 테이블 모델 생성(열 이름 정의)		tableModel = new DefaultTableModel(new Object[] {"이름", "중간고사", "기말고사", "평균", "입력날짜"}, 0);		table = new JTable(tableModel);				// 스크롤		JScrollPane scrollPane = new JScrollPane(table);				// 입력 패널 생성		JPanel inputPanel = new JPanel();		inputPanel.setLayout(new GridLayout(4, 2, 5, 5));				// 이름, 중간시험성적, 기말시험성적 필드 추가		inputPanel.add(new JLabel("이름 : "));		nameField = new JTextField();		inputPanel.add(nameField);		inputPanel.add(new JLabel("중간고사 점수 : "));		midtermField = new JTextField();		inputPanel.add(midtermField);		inputPanel.add(new JLabel("기말고사 점수 : "));		finaltermField = new JTextField();		inputPanel.add(finaltermField);				// 버튼		JPanel buttonPanel = new JPanel();		JButton addButton = new JButton("추가");		addButton.addActionListener(new ActionListener() { // 이벤트 처리			@Override			public void actionPerformed(ActionEvent e) {				addStudent();			}		});		buttonPanel.add(addButton);		JButton deleteButton = new JButton("삭제");		deleteButton.addActionListener(new ActionListener() {			@Override			public void actionPerformed(ActionEvent e) {				deleteStudent();			}		});		buttonPanel.add(deleteButton);		JButton exitButton = new JButton("종료");		exitButton.addActionListener(new ActionListener() {			@Override			public void actionPerformed(ActionEvent e) {				System.exit(0);			}		});		buttonPanel.add(exitButton);				// 전체 Layout 설정		setLayout(new BorderLayout());		add(scrollPane, BorderLayout.CENTER);		add(inputPanel, BorderLayout.NORTH);		add(buttonPanel, BorderLayout.SOUTH);	}		// method 선언	// addStudent(), deleteStudent()	private void addStudent() {							// 학생 성적정보 추가		String name = nameField.getText();					// 학생 이름		String midtermText = midtermField.getText();		// 학생 중간성적		String finaltermText = finaltermField.getText();	// 학생 기말성적		if (name.isEmpty() || midtermText.isEmpty() || finaltermText.isEmpty()) { // 공백으로 입력하였을 경우 에러 메세지			JOptionPane.showMessageDialog(this, "모든정보를 입력하세요", "입력오류", JOptionPane.ERROR_MESSAGE);				return;		}		try { // 자료형 변환			int midtermScore = Integer.parseInt(midtermText);			int finalScore = Integer.parseInt(finaltermText);			double average = (midtermScore + finalScore) / 2.0;						// 현재 날짜 및 시간 구하기			LocalDateTime now = LocalDateTime.now();			// 날짜 포맷 지정			String formatedNow = now.format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss"));						// 테이블 추가			tableModel.addRow(new Object[] {name, midtermScore, finalScore, average, formatedNow});						// 입력 필드(정보) 초기화			nameField.setText("");			midtermField.setText("");			finaltermField.setText("");		} catch(NumberFormatException e) {			JOptionPane.showMessageDialog(this, "성적은 숫자로 입력해야 합니다.", "입력오류", JOptionPane.ERROR_MESSAGE);		}	}	private void deleteStudent() {						// 학생 성적정보 삭제		int selectedRow = table.getSelectedRow();			// 학생성적정보가 있는 줄		if(selectedRow >= 0) {								// 선택한 줄이 1 이상이면			tableModel.removeRow(selectedRow);				// 선택 한 학생정보 줄 삭제		} else { // 선택 안 했을 시 에러 메세지			JOptionPane.showMessageDialog(this, "삭제할 학생정보를 선택하세요.", "삭제오류", JOptionPane.ERROR_MESSAGE);		}	}			public static void main(String[] args) {		SwingUtilities.invokeLater(new Runnable() {			@Override			public void run() {				new StGUI01().setVisible(true);;			}		});	}}